package practiceprograms;

public class EvenNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number=1;
		while(number<=20)
		{
			if((number%2)==0)
			{ 
				System.out.println(number+"\n");
				number++;
			}
			else
				number++;
			
			
		}
		
		
		
	}

}
